class ChessController(val initialConfig: Config) {
    var boardApi: Api? = null
        internal set
}

@Composable
fun Chessboard(controller: ChessController, modifier: Modifier = Modifier) {
    Box(
        listOf(MainBoardStyles, VisualsBoardStyle).toModifier()
            .then(modifier)
            .id("chessground"),
        ref = ref {
            controller.boardApi = Chessground(it, controller.initialConfig)
        }
    )
}


